import React, { Component } from 'react';
import HeaderNavbar from '../HeaderNavbar'
import UserService from '../service/UserService'
import Grid from '@mui/material/Grid';
import CircularProgress from '@mui/material/CircularProgress';
import { Typography, Paper } from '@mui/material';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';

import { resolveResponse } from "../utils/ResponseHandler";
class ProductCategory extends Component {
    constructor(props) {
        super(props);

        this.state = {
            ProductCategory: [],
            waitingFlag: true,
            categoryName: '',
            quantity:0,
            cartList: localStorage.getItem('cartList') ? JSON.parse(localStorage.getItem('cartList')) : []
        }
    }


    componentDidMount() {

        let queryStr = window.location.href.split('?')[1] && window.location.href.split('?')[1].split('=')[1];
        this.setState({ categoryName: decodeURIComponent(queryStr) })
        UserService.getProductCategory(queryStr)
            .then((res) => {
                //   let data = resolveResponse(res); //common response handler
                if (res && res.data) {
                    this.setState({ ProductCategory: res.data, waitingFlag: false })
                }
            });
    }

    handleClick = (productId) => {
        console.log(productId)
        this.props.history.push('/product-details/?id=' + productId);
    }

    capitalizeFirstLetter = (string) => {
        return string.charAt(0).toUpperCase() + string.slice(1);
    }
    quantityChange = (row) => {

        // console.log(e.target.value)
        // if (e.target.value <= 0)
        //     this.setState({ quantity: 1 })
        // else
        //     this.setState({ quantity: e.target.value })    

        console.log(row)

    }
    render() {
        return (
            <React.Fragment>
                <HeaderNavbar />

                <Grid direction="row" container spacing={5} style={{ padding: "20px" }}>

                    <Grid item xs={12} sm={12} >
                        <Typography variant="h5" component="p" >
                            Cart({this.state.cartList.length})
                        </Typography>
                    </Grid>


                    {this.state.cartList && this.state.cartList.map(row => (

                        <Grid item xs={12} sm={8}>
                            <Paper>
                                <Grid direction="row" container spacing={1} style={{ padding: "10px" }}>
                                    <Grid item xs={12} sm={4} >
                                        <img style={{ width: "100px", height: "100px" }} src={row.image} />
                                    </Grid>

                                    <Grid item xs={12} sm={8} >
                                        {row.title} <br /> &#8377; {row.price}
                                        <br /> <br />
                                        <Button variant="contained"> <AddIcon /> </Button>   

                                        <span style={{ width: "100px", height:"20px" }}> {row.quantity} </span>

                                        <Button variant="contained"> <RemoveIcon /> </Button> 

                                    </Grid>
                                </Grid>
                            </Paper>
                        </Grid>

                    ))}


                    {this.state.waitingFlag ? <CircularProgress style={{ padding: "25px" }} /> : ""}
                </Grid>

            </React.Fragment>
        );
    }
}

export default ProductCategory;
