import React, { Component } from 'react';
import HeaderNavbar from '../HeaderNavbar'
import UserService from '../service/UserService'
import Grid from '@mui/material/Grid';
import CircularProgress from '@mui/material/CircularProgress';
import { Typography, Paper } from '@mui/material';

import Button from '@mui/material/Button';

import { resolveResponse } from "../utils/ResponseHandler";
class ProductDetails extends Component {
    constructor(props) {
        super(props);

        this.state = {
            title: '',
            productDetails:"",
            waitingFlag: true,
        }
    }


    componentDidMount() {

       let productId =  window.location.href.split('?')[1] && window.location.href.split('?')[1].split('=')[1]; 
        UserService.getProductDetails(productId)
            .then((res) => {
                //   let data = resolveResponse(res); //common response handler

                console.log(res.data);
                if (res && res.data) {
                    this.setState({ 
                        title: res.data.title, 
                        waitingFlag:false,
                        productDetails : res.data
                    })
                }
            });
    }

    handleClick =(row)=>{
        console.log(row)
        this.props.history.push('/category-product?q='+row);
    }

    // capitalizeFirstLetter =(string) => {
    //     return string.charAt(0).toUpperCase() + string.slice(1);
    // }
    render() {

        let productDetails = this.state.productDetails; 
        console.log("productDetails",productDetails)

        return (
            <React.Fragment>
                <HeaderNavbar />

                <Grid direction="row" container spacing={5} style={{ padding: "20px" }}>

                    {/* <Grid item xs={12} sm={12} >
                        <Typography variant="h5" component="p" >
                           {this.capitalizeFirstLetter(this.state.categoryName)} Product Details
                        </Typography>
                    </Grid> */}

                    <Grid item xs={12} sm={12} style={{ textAlign: "center" }}>
                        <Paper style={{ textAlign: "center", cursor:'pointer', padding:"10px" }} > 
                            <img style={{width:"50%", height:"400px"}} src={productDetails.image} /> <br />
                            {productDetails.title} <br /> &#8377; {productDetails.price} <br /> Ratings {productDetails.rating && productDetails.rating.rate}({productDetails.rating && productDetails.rating.count})
                        </Paper>
                    </Grid>

                    <Grid item xs={12} sm={6} style={{ textAlign: "center" }}>
                        {/* <Paper style={{ textAlign: "center", cursor:'pointer', padding:"10px" }} >  */}

                            {productDetails.title} <br /> &#8377; {productDetails.price} <br /> Ratings {productDetails.rating && productDetails.rating.rate}({productDetails.rating && productDetails.rating.count})
                        {/* </Paper> */}
                    </Grid>

                    {this.state.waitingFlag ? <CircularProgress  /> : ""}
                </Grid>

            </React.Fragment>
        );
    }
}

export default ProductDetails;
