import React, { Component } from 'react';
import HeaderNavbar from './HeaderNavbar'
import UserService from './service/UserService'
import Grid from '@mui/material/Grid';
import CircularProgress from '@mui/material/CircularProgress';
import { Typography } from '@mui/material';

import Button from '@mui/material/Button';

import { resolveResponse } from "./utils/ResponseHandler";
class Homepage extends Component {
    constructor(props) {
        super(props);

        this.state = {
            categoryList: []
        }
    }


    componentDidMount() {

        var data = { allPacks: true, portal: true };
        UserService.getCategory(data)
            .then((res) => {
                //   let data = resolveResponse(res); //common response handler
                if (res && res.data) {
                    this.setState({ categoryList: res.data })
                }
            });

    }
    render() {
        return (
            <React.Fragment>
                <HeaderNavbar />

                <Grid direction="row" container  spacing={1} style={{ paddingLeft: "10px", paddingRight: "10px" }}>
                <Typography variant="h1" component="h2">
  Top Categories
</Typography>;
                    {this.state.categoryList ? this.state.categoryList.map((row) => (
                        <Grid item xs={12} sm={3} style={{textAlign:"center"}}>

                            <img src="https://winaero.com/blog/wp-content/uploads/2019/11/Photos-new-icon.png" /> <br />
                            {row}

                            {/* <Button variant="contained" >
                                Add Pack
                            </Button> */}

                        </Grid>

                    )) : <CircularProgress />}




                </Grid>


            </React.Fragment>
        );
    }
}

export default Homepage;
